<template>
  <div style="display: flex;">
    <div class="container" ref="container"></div>
  </div>
</template>

<script>
import LogicFlow from "@logicflow/core";
import "@logicflow/core/lib/style/index.css";
import "@logicflow/extension/lib/style/index.css";
import { DndPanel, SelectionSelect } from '@logicflow/extension';
import '@logicflow/extension/lib/style/index.css'

// 基础配置
const SilentConfig = {
  stopZoomGraph: false, // 禁止缩放
  stopScrollGraph: false, // 禁止鼠标滚动移动画布
  stopMoveGraph: false, // 禁止移动画布
  adjustEdge: false, // 禁止自动调整边
  adjustEdgeStartAndEnd: false, // 禁止自动调整边起点和终点
  adjustNodePosition: false, // 禁止自动调整节点位置
  hideAnchors: false, // 隐藏锚点
  nodeSelectedOutline: false, // 节点选中时显示轮廓
  nodeTextEdit: false, // 禁止节点文本编辑
  edgeTextEdit: false, // 禁止边文本编辑
  nodeTextDraggable: false, // 禁止节点文本拖拽
  edgeTextDraggable: false, // 禁止边文本拖拽
};


export default {
  name: 'lf-Demo',
  data() {
    return {
      renderData: {
        // 节点数据
        nodes: [
          {
            id: '21', // 节点ID，需要全局唯一，不传入内部会自动生成一个ID
            type: 'rect', // 节点类型，可以传入LogicFlow内置的7种节点类型，也可以注册自定义节点后传入自定义类型
            x: 100, // 节点形状中心在x轴位置
            y: 100, // 节点形状中心在y轴的位置
            text: 'Origin rect', // 节点文本
            properties: { // 自定义属性，用于存储需要这个节点携带的信息，可以传入宽高以重设节点的宽高
              width: 160,
              height: 80,
            }
          },
          {
            id: '50',
            type: 'circle',
            x: 300,
            y: 300,
            text: 'circle',
            properties: {
              r: 60,
            }
          },
        ],
        // 边数据
        edges: [
          {
            id: 'rect-2-circle', // 边ID，性质与节点ID一样
            type: 'polyline', // 边类型
            sourceNodeId: '50', // 起始节点Id
            targetNodeId: '21', // 目标节点Id
          },
        ],
      }
    }
  },
  mounted() {
    this.lf = new LogicFlow({
      container: this.$refs.container,
      grid: true,
      ...SilentConfig,
      // 注册面办插件
      plugins: [DndPanel, SelectionSelect]
    });
    // 用户任务节点
    this.lf.render(this.renderData);

    // 添加控制拖拽面版
    this.lf.extension.dndPanel.setPatternItems([
      {
        type: 'circle',
        text: '开始',
        label: '开始节点',
        icon: 'data:image/png;base64,',
      },  {
        type: 'diamond',
        label: '条件判断',
        icon: 'data:image/png;base64,',
      },
      {
        type: 'circle',
        text: '结束',
        label: '结束节点',
        icon: 'data:image/png;base64,',
      }])
  },
};

</script>

<style scoped>
.container {
  width: 100%;
  height: 720px;
}
</style>